mapper.select:

		SELECT 	*
 		FROM 	( SELECT 	ROWNUM 	RNUM
 		 				  , O.* 
 		 		  FROM 	  ( SELECT 	N.* 
 		 		   				  , MBER_NCNM 
 		 		  			FROM 	BBS 	N 
 		 		  			 	  , MBER 	M 
 		 		  			WHERE 	N.MBER_CODE = M.MBER_CODE 
 		 		  			ORDER 	BY BBSCTT_NO DESC 
 		 		  		  )	O
 		 		)
 		WHERE 	RNUM 	>= #{ start }
 		AND 	ROWNUM 	<= #{ cnt }

mapper.update:
UPDATE BBS
SET 
 	UP_BBSCTT_NO = #{ upBbscttNo }
  , BBSCTT_SJ = #{ bbscttSj }
  , BBSCTT_CN = #{ bbscttCn }
