mapper.select:

		SELECT 	*
 		FROM 	( SELECT 	ROWNUM 	RNUM
 		 				  , O.* 
 		 		  FROM 	  ( SELECT 	* 
 		 		  			FROM 	REFND_HIST
 		 		  			WHERE 	RCORD_DT >= TO_TIMESTAMP(#{ startRcordDt })
 		 		  			AND 	RCORD_DT <  TO_TIMESTAMP(#{ endRcordDt })
 		 		  			ORDER 	BY RCORD_DT DESC 
 		 		  		  )	O
 		 		) 
 		WHERE 	RNUM 	>= #{ start }
 		AND 	ROWNUM 	<= #{ cnt }


		SELECT 	*
 		FROM 	( SELECT 	ROWNUM 	RNUM
 		 				  , O.* 
 		 		  FROM 	  ( SELECT 	* 
 		 		  			FROM 	REFND_HIST
 		 		  			WHERE 	${ conditionType } = #{ condition }
 		 		  			ORDER 	BY RCORD_DT DESC 
 		 		  		  )	O
 		 		) 
 		WHERE 	RNUM 	>= #{ start }
 		AND 	ROWNUM 	<= #{ cnt }
