mapper.select:

		SELECT 	*
 		FROM 	( SELECT 	ROWNUM 		RNUM
 		 				  , O.* 
 		 		  FROM 	  ( SELECT 		T.TAG_CODE 			
							 		  , TAG_NM 
							 		  , IMAGE_ADRES 
							 		  , TAG_READ_CNT 
							FROM 		TAG 				T
							 		  , TAG_READ_CNT 		TR
							WHERE 		T.TAG_CODE 			= TR.TAG_CODE
							AND 		${ conditionType } 	LIKE #{ condition }
							ORDER BY 	T.TAG_CODE
 		 		  		  )	O
 		 		) 

 		WHERE 	RNUM 	>= #{ start }
 		AND 	ROWNUM 	<= #{ cnt }

SELECT 	COUNT(*) CNT
FROM 	TAG_SEARCH
WHERE 	TAG_CODE = #{ tagCode }
